mapper.select:
select 'mem' from member
where member_id = #{userId} and member_phone = #{userPhone} and member_email = #{userEmail}
union
select 'emp' from employees
where emp_id = #{userId} and emp_phone = #{userPhone} and emp_email = #{userEmail}

select member_Email from member
where member_id = #{memId}
union 
select emp_email from employees
where emp_id = #{empId}
