mapper.select:
select member_id userId, member_pw userPw, 'mem' grade, member_ok memOk
from member 
where member_id = #{userId}
union 
select emp_id, emp_pw, 'emp', null from employees where emp_id = #{userId}

select member_Email from member
where member_id = #{memId}
union 
select emp_email from employees
where emp_id = #{empId}
