mapper.select:
select professor_num professorNum, department_num departmentNum, professor_id professorId, 
professor_pw professorPw, professor_name professorName,	professor_phone professorPhone, 
professor_email professorEmail from professor where professor_id = #{professorId}

mapper.update:
update professor set professor_num =#{professorNum}, department_num=#{departmentNum}, professor_id =#{professorId}, 
professor_name =#{professorName}, professor_phone =#{professorPhone}, professor_email =#{professorEmail}
where professor_num = #{professorNum}
